import React from 'react';

import {counterContext} from '../editor/counter-context.js';

class Counter extends React.Component{
  static contextType = counterContext;
  render(){


  const styles = {
    container: {
      alignItems: 'center',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-evenly',
      width: '40%',
    },
    counter: {
      display: 'inline-block',
      fontSize: '1em',
      border: '2px solid #444',
      borderRadius: '50%',
      lineHeight: '5em',
      textAlign: 'center',
      width: '5em',
    },
    button: {
      height: '3em',
    },
  };

  return (
    <div style={styles.container}>
      <button style={styles.button} onClick={() => this.counterContext.increment}> + </button>
      <span style={styles.counter}>{this.context.count}</span>

      <button style={styles.button} onClick={() => this.CounterContext.decrement}> - </button>
    </div>
  );
};
}

export default Counter;
